/* WELCOME TO KERNEL PROGRAMMING!
 *
 * What is this program?
 * - This is a "kernel module" - code that runs inside the Linux operating system
 * - Think of it like adding a small program to your computer's core brain
 * - When you load this module, it will print a message you can see with 'dmesg'
 * 
 * Don't worry if you don't understand everything - just follow the TODOs!
 * Programming is learned by doing, step by step.
 */

#include <linux/module.h>    /* For all kernel modules */
#include <linux/kernel.h>    /* For printk() function */
#include <linux/init.h>      /* For __init and __exit macros */

/* TODO: Create the init function */
/* This function runs when the module is loaded into memory */
/* 'static' means only this file can use this function */
/* 'int' means this function returns a number (0 = success) */
static int __init hello_init(void)
{
    /* TODO: Print "Hello World from kernel!" using printk */
    /* printk is like printf, but for kernel messages */
    /* KERN_INFO means "information level" message */
    /* \n means "new line" (like pressing Enter) */
    /* Use: printk(KERN_INFO "Your message here\n"); */

    return 0;  /* Return 0 means "success, everything worked!" */
}

/* TODO: Create the exit function */
/* This function runs when the module is unloaded from memory */
/* 'void' means this function doesn't return anything */
static void __exit hello_exit(void)
{
    /* TODO: Print "Goodbye from kernel!" using printk */
    /* This lets us know the module was unloaded successfully */
    /* Use: printk(KERN_INFO "Your message here\n"); */
}

/* TODO: Tell the kernel which functions to call */
/* These lines register your functions with the kernel */
/* Remove the /* and */ around these lines to uncomment them: */
/* module_init(hello_init);   <- Uncomment this line */
/* module_exit(hello_exit);   <- Uncomment this line */

/* Module information - these are required by the kernel */
/* Think of these like a "name tag" for your module */
MODULE_LICENSE("GPL");     /* GPL is a common open source license */
MODULE_AUTHOR("Student");   /* That's you! */
MODULE_DESCRIPTION("My first kernel module - Hello World");
MODULE_VERSION("1.0");     /* Version 1.0 - your first try! */
