#include <stdio.h>
#include <stdlib.h>
#include <time.h>
#include <string.h>

int main() {
    printf("=== Dynamic Structure Implementation Test ===\n");

    // Seed random number generator
    srand(time(NULL));

    // Generate random student data
    int test_id1 = (rand() % 9000) + 10000;  // 10000-18999
    int test_grade1 = (rand() % 20) + 80;    // 80-99
    int test_id2 = (rand() % 9000) + 20000;  // 20000-28999
    int test_grade2 = (rand() % 30) + 60;    // 60-89

    printf("Test 1: Testing with student_id=%d, student_grade=%d\n", test_id1, test_grade1);
    char cmd1[256];
    snprintf(cmd1, sizeof(cmd1), "insmod /lib/modules/structures.ko student_id=%d student_grade=%d", test_id1, test_grade1);
    system("rmmod structures 2>/dev/null");
    system(cmd1);

    printf("Test 2: Testing with student_id=%d, student_grade=%d\n", test_id2, test_grade2);
    char cmd2[256];
    snprintf(cmd2, sizeof(cmd2), "insmod /lib/modules/structures.ko student_id=%d student_grade=%d", test_id2, test_grade2);
    system("rmmod structures");
    system(cmd2);

    printf("SUCCESS: Dynamic structure test completed\n");
    return 0;
}
