#ifndef DYNAMIC_MEMORY_H
#define DYNAMIC_MEMORY_H

#include <linux/module.h>
#include <linux/kernel.h>
#include <linux/init.h>
#include <linux/types.h>
#include <linux/slab.h>

/* ========================================================================== */
/* PATTERN SCAFFOLDING: WHEN to Use Static vs Dynamic Memory Allocation */
/* ========================================================================== */
/* This problem teaches the DECISION-MAKING PROCESS for choosing between
 * static allocation and dynamic allocation (kmalloc).
 *
 * CRITICAL LEARNING OBJECTIVE:
 * Understanding WHEN kmalloc is REQUIRED vs OPTIONAL is essential for
 * kernel development. Many students learn the syntax but not the criteria!
 *
 * DECISION TREE:
 * 
 * Q1: Is the size known at compile time?
 *     NO  -> MUST use kmalloc (required)
 *     YES -> Continue to Q2
 *
 * Q2: Does memory need to outlive the function scope?
 *     YES -> MUST use kmalloc (required)
 *     NO  -> Continue to Q3
 *
 * Q3: Do you need to return a pointer from a function?
 *     YES -> MUST use kmalloc (required)
 *     NO  -> Static allocation is acceptable
 *
 * EXAMPLES OF WHEN KMALLOC IS REQUIRED:
 *
 * 1. RUNTIME-DETERMINED SIZE (Cannot use static allocation):
 *    int count = module_parameter;  // Unknown at compile time
 *    Device devices[count];  // ILLEGAL in C (VLA not allowed in kernel)
 *    Device *devices = kmalloc(sizeof(Device) * count, GFP_KERNEL);  // REQUIRED
 *
 * 2. RETURNING MEMORY FROM FUNCTION (Cannot use local variables):
 *    Device* bad_create(void) {
 *        Device local = {1, 2, true};  // Stack variable destroyed on return
 *        return &local;  // DANGER: Dangling pointer!
 *    }
 *    Device* good_create(void) {
 *        Device *dev = kmalloc(sizeof(Device), GFP_KERNEL);  // REQUIRED
 *        return dev;  // SAFE: Heap memory persists
 *    }
 *
 * 3. PERSISTENT LIFETIME (Beyond function scope):
 *    void init_device(void) {
 *        Device temp = {1, 2, true};  // Destroyed when function exits
 *    }
 *    Device *persistent_device = kmalloc(sizeof(Device), GFP_KERNEL);  // REQUIRED
 *
 * EXAMPLES OF WHEN STATIC IS ACCEPTABLE:
 *
 * 1. FIXED SIZE, KNOWN AT COMPILE TIME:
 *    Device single_device = {1001, 200, true};  // OK: Size known, lifetime OK
 *
 * 2. FIXED-SIZE ARRAY:
 *    Device devices[5] = {...};  // OK: Size known at compile time
 *
 * WHY THIS MATTERS IN KERNEL DEVELOPMENT:
 * - The Linux kernel uses kmalloc extensively for runtime-sized data structures
 * - Driver registration, device management, buffer allocation all use kmalloc
 * - Misunderstanding leads to stack corruption, memory leaks, crashes
 */

/* ========================================================================== */
/* MEMORY ALLOCATION PATTERN: The Lifecycle */
/* ========================================================================== */
/* KMALLOC/KFREE LIFECYCLE (4 Essential Steps):
 *
 * Step 1: ALLOCATE
 *   Device *dev = kmalloc(sizeof(Device), GFP_KERNEL);
 *   ^       ^      ^       ^               ^
 *   type    result function size           flags
 *
 * Step 2: CHECK (Critical - kmalloc can fail!)
 *   if (!dev) {
 *       printk(KERN_ERR "Allocation failed!\n");
 *       return -ENOMEM;
 *   }
 *
 * Step 3: USE
 *   dev->device_id = 1001;
 *   dev->status = 200;
 *   dev->is_active = true;
 *
 * Step 4: FREE (When done)
 *   kfree(dev);
 *   dev = NULL;  // Prevent use-after-free bugs
 *
 * COMMON MISTAKES:
 * - Forgetting to check if kmalloc returned NULL
 * - Using memory after kfree (use-after-free bug)
 * - Forgetting to kfree (memory leak)
 * - Double-freeing the same pointer (double-free bug)
 */

/* Device structure definition */
typedef struct {
    int device_id;
    int status;
    bool is_active;
} Device;

/* Global device array pointer - dynamically allocated */
extern Device **device_array;
extern int device_count;

/* ========================================================================== */
/* SECTION 1: Function Declarations */
/* ========================================================================== */
/* TODO: Declare the following functions (declaration only, no implementation) */
/* 
 * REQUIRED FUNCTIONS:
 * 1. allocate_devices - Takes device count, returns 0 on success, -ENOMEM on failure
 * 2. initialize_device - Takes device pointer and ID, initializes device fields
 * 3. print_device_info - Takes device pointer, prints device information
 * 4. cleanup_devices - Takes no parameters, frees all allocated memory
 *
 * HINTS:
 * - allocate_devices should return int (for error code)
 * - initialize_device should take (Device *dev, int id) as parameters
 * - print_device_info should take (Device *dev) as parameter
 * - cleanup_devices should return void
 */

#endif /* DYNAMIC_MEMORY_H */

#include "dynamic_memory.h"

/* Global device array pointer - starts as NULL */
Device **device_array = NULL;
int device_count = 3;

/* IMPORTANT: DO NOT REMOVE OR MODIFY THE LINES BELOW!
 * These module parameter declarations are required for validation testing.
 * The testing system uses device_count to verify your dynamic allocation
 * works with RUNTIME-DETERMINED sizes (the key learning objective!).
 */
module_param(device_count, int, 0644);
MODULE_PARM_DESC(device_count, "Number of devices to allocate (runtime-determined)");
/* END OF REQUIRED MODULE PARAMETER SECTION */

/* ========================================================================== */
/* PATTERN SCAFFOLDING: WHY Static Allocation Fails Here */
/* ========================================================================== */
/* PROBLEM: We need to allocate 'device_count' devices, but device_count
 * is determined at MODULE LOAD TIME (runtime), not compile time!
 *
 * STATIC ALLOCATION ATTEMPT (WILL NOT WORK):
 *   Device devices[device_count];  // ILLEGAL: VLA not allowed in kernel!
 *   
 * WHY THIS FAILS:
 * - device_count is only known when module loads (insmod dynamic_memory.ko device_count=5)
 * - C requires array sizes to be known at compile time
 * - Variable-Length Arrays (VLAs) are prohibited in kernel code for safety
 *
 * SOLUTION: MUST use kmalloc for runtime-determined sizes!
 *   Device **devices = kmalloc(sizeof(Device*) * device_count, GFP_KERNEL);
 *   ^       ^          ^        ^                  ^
 *   type    result     function size calculation   flags
 *
 * This is a REQUIRED use case for dynamic memory allocation!
 */

/* ========================================================================== */
/* PATTERN SCAFFOLDING: Two-Level Allocation Pattern */
/* ========================================================================== */
/* For an array of device pointers, we need TWO allocations:
 *
 * LEVEL 1: Allocate the array of pointers
 *   Device **device_array = kmalloc(sizeof(Device*) * count, GFP_KERNEL);
 *   This creates: [ptr0][ptr1][ptr2]...[ptrN]  (array of pointers)
 *
 * LEVEL 2: Allocate each individual device
 *   for (i = 0; i < count; i++) {
 *       device_array[i] = kmalloc(sizeof(Device), GFP_KERNEL);
 *   }
 *   This creates: [ptr0] -> Device{id, status, active}
 *                 [ptr1] -> Device{id, status, active}
 *                 [ptr2] -> Device{id, status, active}
 *
 * MEMORY LAYOUT VISUALIZATION:
 *   Stack:                    Heap (Level 1):           Heap (Level 2):
 *   +-------------+          +-------+                 +---------+
 *   |device_array |--------->| ptr0  |---------------->| Device 0|
 *   +-------------+          | ptr1  |---------------->| Device 1|
 *                            | ptr2  |---------------->| Device 2|
 *                            +-------+                 +---------+
 *
 * CLEANUP MUST BE TWO-LEVEL (in reverse order):
 *   Step 1: Free each device (Level 2)
 *   Step 2: Free the pointer array (Level 1)
 */

/* ========================================================================== */
/* SECTION 1: Memory Allocation Function */
/* ========================================================================== */
/* TODO: Implement allocate_devices function
 *
 * REQUIREMENTS:
 * - Function signature: int allocate_devices(int count)
 * - Allocate array of Device pointers (Level 1)
 * - Allocate each individual Device (Level 2)
 * - Check BOTH allocations for NULL (error handling!)
 * - Return 0 on success, -ENOMEM on failure
 * - Store result in global device_array variable
 *
 * ALGORITHM:
 * 1. Print message: "Allocating %d devices..." with count
 * 2. Allocate Level 1: device_array = kmalloc(sizeof(Device*) * count, GFP_KERNEL)
 * 3. Check if device_array is NULL (allocation failed)
 *    - If NULL: print error, return -ENOMEM
 * 4. Loop i from 0 to count-1:
 *    a. Allocate Level 2: device_array[i] = kmalloc(sizeof(Device), GFP_KERNEL)
 *    b. Check if device_array[i] is NULL
 *       - If NULL: free all previously allocated devices (0 to i-1), free device_array, return -ENOMEM
 * 5. Print success message: "Successfully allocated %d devices"
 * 6. Return 0
 *
 * CRITICAL ERROR HANDLING:
 * If Level 2 allocation fails partway through (e.g., device 3 out of 5),
 * you MUST free devices 0, 1, 2 before returning!
 */
int allocate_devices(int count)
{
    /* TODO: Implement two-level allocation as described above */
    return -ENOMEM;  /* Replace with actual implementation */
}

/* ========================================================================== */
/* SECTION 2: Device Initialization Function */
/* ========================================================================== */
/* TODO: Implement initialize_device function
 *
 * REQUIREMENTS:
 * - Function signature: void initialize_device(Device *dev, int id)
 * - Check if dev pointer is NULL (safety check)
 * - Initialize device fields:
 *   - device_id = id
 *   - status = 200
 *   - is_active = true
 * - Print message: "Device %d initialized"
 *
 * ALGORITHM:
 * 1. Check if dev is NULL
 *    - If NULL: print error "Cannot initialize NULL device", return
 * 2. Set dev->device_id = id
 * 3. Set dev->status = 200
 * 4. Set dev->is_active = true
 * 5. Print success message with device ID
 */
void initialize_device(Device *dev, int id)
{
    /* TODO: Implement device initialization as described above */
}

/* ========================================================================== */
/* SECTION 3: Device Info Printing Function */
/* ========================================================================== */
/* TODO: Implement print_device_info function
 *
 * REQUIREMENTS:
 * - Function signature: void print_device_info(Device *dev)
 * - Check if dev pointer is NULL (safety check)
 * - Print device information:
 *   - "=== Device Info ==="
 *   - "Device ID: %d" (dev->device_id)
 *   - "Status: %d" (dev->status)
 *   - "Active: YES" or "Active: NO" (based on dev->is_active)
 *
 * ALGORITHM:
 * 1. Check if dev is NULL
 *    - If NULL: print error "Cannot print info: device is NULL", return
 * 2. Print header "=== Device Info ==="
 * 3. Print "Device ID: %d" with dev->device_id
 * 4. Print "Status: %d" with dev->status
 * 5. Print "Active: YES" if dev->is_active is true, else "Active: NO"
 */
void print_device_info(Device *dev)
{
    /* TODO: Implement device info printing as described above */
}

/* ========================================================================== */
/* SECTION 4: Memory Cleanup Function */
/* ========================================================================== */
/* TODO: Implement cleanup_devices function
 *
 * REQUIREMENTS:
 * - Function signature: void cleanup_devices(void)
 * - Free all allocated memory (two-level cleanup!)
 * - Set pointers to NULL after freeing (prevent use-after-free)
 * - Use global variables: device_array, device_count
 *
 * ALGORITHM:
 * 1. Check if device_array is NULL
 *    - If NULL: print "No devices to free", return
 * 2. Print message: "Freeing %d devices..." with device_count
 * 3. Loop i from 0 to device_count-1:
 *    a. Check if device_array[i] is not NULL
 *    b. Free device_array[i] using kfree
 *    c. Set device_array[i] = NULL
 * 4. Free device_array using kfree
 * 5. Set device_array = NULL
 * 6. Print success message: "All devices freed"
 *
 * CRITICAL: Must free in REVERSE order of allocation!
 * - First free individual devices (Level 2)
 * - Then free pointer array (Level 1)
 */
void cleanup_devices(void)
{
    /* TODO: Implement two-level cleanup as described above */
}

/* ========================================================================== */
/* MODULE INIT/EXIT */
/* ========================================================================== */
static int __init dynamic_memory_init(void)
{
    int ret;
    int i;
    int base_id = 1000;

    printk(KERN_INFO "Dynamic memory module loaded\n");
    printk(KERN_INFO "Runtime device count: %d\n", device_count);

    /* Allocate devices dynamically */
    ret = allocate_devices(device_count);
    if (ret != 0) {
        printk(KERN_ERR "Failed to allocate devices\n");
        return ret;
    }

    /* Initialize all devices */
    printk(KERN_INFO "Initializing %d devices...\n", device_count);
    for (i = 0; i < device_count; i++) {
        initialize_device(device_array[i], base_id + i);
    }

    /* Print information for all devices */
    printk(KERN_INFO "Printing device information...\n");
    for (i = 0; i < device_count; i++) {
        printk(KERN_INFO "Device %d:\n", i);
        print_device_info(device_array[i]);
    }

    printk(KERN_INFO "Dynamic allocation demonstration completed\n");
    return 0;
}

static void __exit dynamic_memory_exit(void)
{
    printk(KERN_INFO "Dynamic memory module unloading\n");

    /* Clean up all allocated memory */
    cleanup_devices();

    printk(KERN_INFO "Module unloaded cleanly\n");
}

module_init(dynamic_memory_init);
module_exit(dynamic_memory_exit);

MODULE_LICENSE("GPL");
MODULE_AUTHOR("Student");
MODULE_DESCRIPTION("Dynamic memory allocation with runtime-determined sizes");
MODULE_VERSION("1.0");

