#include <stdio.h>
#include <stdlib.h>
#include <time.h>

int main() {
    printf("=== Runtime-Determined Size Allocation Test ===\n");

    srand(time(NULL));

    // Test different device counts (runtime-determined)
    int test_count1 = (rand() % 3) + 2;  // 2-4 devices
    int test_count2 = (rand() % 4) + 5;  // 5-8 devices

    printf("Test 1: Allocating %d devices (runtime size)\n", test_count1);
    char cmd1[256];
    snprintf(cmd1, sizeof(cmd1), "insmod /lib/modules/dynamic_memory.ko device_count=%d", test_count1);
    system("rmmod dynamic_memory 2>/dev/null");
    system(cmd1);

    printf("Test 2: Allocating %d devices (runtime size)\n", test_count2);
    char cmd2[256];
    snprintf(cmd2, sizeof(cmd2), "insmod /lib/modules/dynamic_memory.ko device_count=%d", test_count2);
    system("rmmod dynamic_memory");
    system(cmd2);

    printf("SUCCESS: Runtime-sized allocation test completed\n");
    return 0;
}
